import { Box } from "@mui/system";
import "./Home.css";
import React from "react";
import { Paper, Typography, IconButton, Button, Stack, Divider, CircularProgress,   } from "@mui/material";
import { Close } from "@mui/icons-material";
import { styled, useTheme } from '@mui/material/styles';
import Card from '@mui/material/Card';
import CardHeader from '@mui/material/CardHeader';
import CardMedia from '@mui/material/CardMedia';
import CardContent from '@mui/material/CardContent';
import CardActions from '@mui/material/CardActions';
import { useGetproductsByNameQuery } from '../../Redux/productsApi'



const Home = () => {
  const theme=useTheme();
  const { data, error, isLoading } = useGetproductsByNameQuery()

  if (isLoading){
    return(
      <Box sx={{ display: 'flex' }}>
      <CircularProgress />
    </Box>
    )
  }


  console.log(data)

  if (data){
    return (

        <Stack direction={'row'} sx={{flexWrap:'wrap', justifyContent:'center'}}>

          {data.map((item) => {
            return(

              <Card key={item.id} sx={{ maxWidth: 277,mb:6,mx:2 }}>
          <CardHeader


            title="Alzubbara"

          />
          <CardMedia
            component="img"
            height="277"
            image={item.imageLink}
            alt="Paella dish"
          />
          <CardContent>
            <Typography variant="body2" color="text.secondary">
              {item.description}
            </Typography>
          </CardContent>
          <CardActions sx={{justifyContent:'space-between'}} disableSpacing>
            <Button sx={{textTransform:'capitalize'}} variant="text" color="primary">
              add to Cart
            </Button>
            <Typography mr={1} variant="body1" color={theme.palette.error.light}>$ </Typography>
          </CardActions>

        </Card>
            )

          })}

        </Stack> 
  );
  }
}

export default Home;
